import React, { Component } from 'react'
import './blogForm.css';


export default class blogForm extends Component {

    constructor(props) {
        super(props);
            this.state = {
                email           : "",
                password        : "",
                errors          : {},
                isValid     : false

            };
    }


    handleChange = (event)=>{
        var name = event.currentTarget.name;
        var value = event.currentTarget.value;              
        this.setState({ [name] : value}
        ); 
    }

    render() {
        return (
            <div>
                <div className="BlogWrapper container-fluid">
                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                        <div className="col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-12 col-xs-12 blogFormWrapper">
                            <div className="blogHeading"> 
                                <h3 className="boldFont text-left">Blog Form</h3>
                            </div>
                            <hr/>
                            <form>
                                <div className="input-group-mb-0">  
                                    <div className="col-lg-12 col-md-12">
                                        <div className="col-lg-6 col-md-6 input-group mt60">
                                            <span className="input-group-addon"><i className="glyphicon glyphicon-user"></i></span>
                                            <input id="fullName" type="text" className="form-control" name="fullName" placeholder="Full Name" onChange  = {this.handleChange.bind(this)} required/>
                                        </div>

                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
