import React, { Component } from 'react';
import './organizationForm.css';

import Axios from 'axios';
import Swal  from 'sweetalert2';

export default class organizationForm extends Component {

    constructor(props) {
        super(props);
            this.state = {
                OrgName     : "",
                OrgSortName : "",
                OrgLogoUrl  : "",
                OrgLogo     : "",   
                errors      : {},
                isValid     : false

        };
    }

    handleChange = (event)=>{
        var name = event.currentTarget.name;
        var value = event.currentTarget.value;              
        this.setState({ [name] : value}
        );
    }
    validUrlHandleChange =(event) =>{
        // const regex = /^((ftp|http|https):\/\/)?(www.)?(?!.*(ftp|http|https|www.))[a-zA-Z0-9_-]+(\.[a-zA-Z]+)+((\/)[\w#]+)*(\/\w+\?[a-zA-Z0-9_]+=\w+(&[a-zA-Z0-9_]+=\w+)*)?$/gm;
        const regex = ('^(https?:\\/\\/)?'+ // protocol
            '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|'+ // domain name
            '((\\d{1,3}\\.){3}\\d{1,3}))'+ // OR ip (v4) address
            '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*'+ // port and path
            '(\\?[;&a-z\\d%_.~+=-]*)?'+ // query string
            '(\\#[-a-z\\d_]*)?$','i');
        if(event.target.value.match(regex)){
            this.setState({
                [event.target.name]:event.target.value,
                isValid:true
            })
        }else{
            this.setState({
                [event.target.name]:event.target.value,
                isValid:false
            })
        }
    }
    validate(){
        let input = this.state;
        console.log("input", input);
        let errors = {};
        let isValid = true;
        if (!input["OrgName"]) {
          isValid = false;
          errors["OrgName"] = "Please Enter Organiation Name";
        }
        if (!input["OrgSortName"]) {
          isValid = false;
          errors["OrgSortName"] = "Please Enter Organiation Sort Name";
        }
        if (!input["OrgLogoUrl"]) {
          isValid = false;
          errors["OrgLogoUrl"] = "Please Enter Organiation Logo Url";
        }
        if (!input["OrgLogo"]) {
          isValid = false;
          errors["OrgLogo"] = "Please Enter Organiation Logo ";
        }
        this.setState({
          errors: errors
        });
        return isValid;
    }  

    Submit(event){
        event.preventDefault();
        if(this.validate()){

            var formvalues = {
                organizationName       : this.state.OrgName,
                organizationShortName  : this.state.OrgSortName,
                organizationURL        : this.state.OrgLogoUrl,
                organizationLOGO       : this.state.OrgLogo,
            }
            Axios.post('http://122.170.12.63:90/api/Organization/addOrganization', formvalues)
            .then((response) => {
                console.log(response)
                console.log(response.data.data)
                if(response.data.message == "Success"){
                    Swal.fire("Congrats!","Organization Added Successfully","success");
                    this.props.history.push("/get-list")
                }else{
                    Swal.fire("Oops!","Please Submit Valid Details","warning");
                }
            })
            .catch(error =>{
                Swal.fire("Sorry!","Please Enter Valid UserId or Password","warning");
            })
        }
    }

    render() {
        return (
            <div className="organizationWrapper">
                <div className="col-lg-12">   
                    <div className="col-lg-6 col-lg-offset-3 marginTop60 Height-box-shadow">
                        <h3 className="boldFont mt20">Add Organization</h3>
                        <hr/>
                    <div className="col-lg-12">
                        <form className="input-group-mb-0">
                            <div className="col-lg-12 input-group mt30">
                                <span className="input-group-addon"><i className="glyphicon glyphicon-user"></i></span>
                                <input  type="text" className="form-control" name="OrgName" maxlength="100" placeholder="Organization Name" 
                                    onChange  = {this.handleChange.bind(this)} 
                                    value = {this.state.OrgName}
                                    required/>
                            </div>
                            <div className="text-danger">{this.state.errors ? this.state.errors.OrgName : ''}</div>
                            <div className="col-lg-12 input-group">
                                <span className="input-group-addon"><i className="glyphicon glyphicon-user"></i></span>
                                <input  type="text" className="form-control" name="OrgSortName" maxlength="50" placeholder="Organization Sort Name" 
                                    onChange  = {this.handleChange.bind(this)} 
                                    value     = {this.state.OrgSortName}
                                    required
                                />
                            </div>
                            <div className="text-danger">{this.state.errors ? this.state.errors.OrgSortName : ''}</div>    
                            <div className="col-lg-12 input-group">
                                <span className="input-group-addon"><i className="glyphicon glyphicon-scale"></i></span>
                                <input  type="text" className="form-control" name="OrgLogoUrl" placeholder="Organization Logo URL"
                                    onChange  = {this.validUrlHandleChange.bind(this)} 
                                    value     = {this.state.OrgLogoUrl}
                                    required
                                />
                            </div>
                            {/*<div className="text-danger">{this.state.errors ? this.state.errors.OrgLogoUrl : ''}</div>    */}
                            {!this.state.isValid ? <div className="text-danger">Please Enter Valid Url</div>  :<div className="text-left"> Url is Valid</div> }
                            <div className="col-lg-12 input-group">
                                <span className="input-group-addon"><i className="glyphicon glyphicon-scale"></i></span>
                                <input  type="text" className="form-control" name="OrgLogo" placeholder="Organization Logo" 
                                    onChange  = {this.handleChange.bind(this)} 
                                    value     = {this.state.OrgLogo}
                                    required/>
                            </div>
                            <div className="text-danger">{this.state.errors ? this.state.errors.OrgLogo : ''}</div>
                            <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 button padZero">
                                <button type="button" className="col-lg-2  btn btn-success loginbutton"onClick={this.Submit.bind(this)}>Save</button>
                                <button type="button" className="col-lg-2 ml20px btn btn-danger loginbutton">Cancel</button>
                            </div> 
                        </form>
                    </div>
                    </div>
                </div>
           </div>
        )
    }
}
